import React from 'react';

const NoteForm = (props)=> {
const { fromTitle,title,content,titleChanged,contentChanged,submitClicked,submitText}=props;
return (
<div>
<h2>{fromTitle}</h2>
<div>
<input
type="text"
name="title"
className="from-input mb-30"
placeholder="العنوان"
value={title}
onChange={titleChanged}
/>
<textarea
rows="10"
name="content"
className="from-input mb-30"
placeolder='العنوان'
value={content}
onchange={titleChanged}
/>

</div>

</div>

)
}

export default NoteForm; // تصدير المكون
