import React, { useState } from 'react';
import './App.css';
import Preview from './compoents/Preview'; {/* 04:48كاين كلام حول طريقة الاستيراد الفيديو 05 إضافة ملاحظه الدقيقه */ }
import Message from './compoents/Message';
import NotesContainer from './compoents/Notes/NotesContainer';
import NotesList from './compoents/Notes/NotesList';
import Note from './compoents/Notes/Note'


function App() {
  console.log("hi all ");
  const [notes, setNotes] = useState([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [selectNote, setselecNote] = useState(null);
  const [creating, setCreating] = useState(false);
  const [editing, setEditing] = useState(false);
  // تغيير عنوان الملاحظه 
  const changeTitleHandler = (event) => {
    setTitle(event.target.value);
  }
  // تغيير  محتوى  الملاحظه
  const changeContentHandler = (event) => {
    setContent(event.target.value);
  }

  // حفظ  الملاحظه   
  const saveNoteHandler = () => {
    const note = {
      id: new Date(),
      title: title,
      content: content

    }

    const updateNotes = [...notes, note]; // معامل النشر es6
    setNotes(updateNotes);
    setCreating(false);
    setselecNote(note.id);

    setTitle('');
    setContent('');

  }
  // اختيار الملاحظه
  const selectNoteHandler = noteId => {
    setselecNote(noteId);
    setCreating(false);
    setEditing(false);

  }
  // الانتقال الى وضع تعديل ملاحظه
  const editNoteHandler = () => {
    console.log("is clecked ");
    const note = notes.find(note => note.id === selectNote);
    setEditing(true);
    setTitle(note.title);
    setContent(note.content);

  }
  // تعديل ملاحظه 
  const updateNotesHandeler = () => {
    const updateNotesHandeler = [...notes];
    const noteIndex = notes.findIndex(note => note.id === selectNote);
    updateNotes[noteIndex] = {
      id: selectNote,
      title: title,
      content: content
    };
    setNotes(updateNotes);
    setCreating(true);
    setEditing(true);
    setTitle('');
    setContent('');

  }
  // الانتقال الى واجهة تعديل ملاحظه
  const addNoteHandler = () => {
    setCreating(true);
    setEditing(false);
    setTitle('');
    setContent('');
  }

  const getAddNote = () => {
    return (
      <div>
        <h2>إضافة ملاحظة جديدة</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="العنوان"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="النص"
            value={content}
            onChange={changeContentHandler}
          />

          <a href="#" className="button green" onClick={saveNoteHandler}>
            حفظ
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {
    if (notes.length === 0) {
      return <Message title="لا يوجد ملاحظه لعرضها " />   /*  hg 15:50 لاحظ لم نخطط للمكون ميساج قبل البدأ  hhg  */
    }
    if (!selectNote) {
      return <Message title="الرجاء اختيار ملاحظه" />
    }

    const note = notes.find(note => {   // تكلم عن هذه اداله في فديو اضافة ملاحظه في جوايه  16:30
      return note.id === selectNote;
    });

    let noteDisplay = (
      <div>
        <h2>{note.title}</h2>
        <p>{note.content}</p>

      </div>
    )

    if (editing) {
      console.log('from if edating test ');
      <div>
        <h2>تعديل ملاحظة </h2>
        <form>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="العنوان"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="النص"
            value={content}
            onChange={changeContentHandler}
          />

          <a href="#" className="button green" onClick={updateNotesHandeler}>
            حفظ
          </a>
        </form>
      </div>





    }

    return (
      <div>
        {!editing &&
          <div className="note-operations">
            <a href="#" onClick={editNoteHandler} >
              <i className="fa fa-pencil-alt" />
            </a>
            <a href="#">
              <i className="fa fa-trash" />
            </a>

          </div>
        }
        {noteDisplay}

      </div>
    );
  };


  return (
    <div className="App">
      <NotesContainer>
        <NotesList>
          {notes.map(note =>
            <Note
              key={note.id}
              title={note.title}
              noteClicked={() => selectNoteHandler(note.id)}
              active={selectNote === note.id}
            />
          )}

        </NotesList>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
      </NotesContainer>
      <Preview>
        {creating ? getAddNote() : getPreview()}
      </Preview>
    </div>
  );
}

export default App;
