import React from 'react';


const NoteForm =(props)=>{
  const { fromTile,title,content,titleChanged,contentChanged,submitClicked,submitText} = props;
  return (
<div>
<h2>  {fromTile}</h2>
<div>
<input
type="text"
name="titLe"
className="form-input mb-30"
placeholder="العنوان"
value={title}
onChange={titleChanged}
/>
<textarea
  rows="10"
  name="content"
  className="from-input"
  placeholder="النص"
  value={content}
  onChange={contentChanged}
/>
<a href='#' className="button green" onClick={submitClicked} >{submitText}</a>
</div>

</div>

  );

}
