import React, { useState } from 'react';
import './App.css';
import Preview from './compoents/Preview'; {/* 04:48كاين كلام حول طريقة الاستيراد الفيديو 05 إضافة ملاحظه الدقيقه */}
import Message from './compoents/Message';

function App() {

  const [notes,setNotes]= useState([]);
  const [title,setTitle]= useState('');
  const [content,setContent]= useState('');
  const [selectNote,setselecNote]= useState(null);
  const [creating,setCreating ]= useState(false);
  const [edating,setEdating]= useState(false);
// تغيير عنوان الملاحظه 
  const changeTitleHandler = (event) => {
  setTitle(event.target.value);
  }
// تغيير  محتوى  الملاحظه
  const changeContentHandler = (event) =>{
  setContent(event.target.value);
  }

// حفظ  الملاحظه   
const saveNoteHandler = () => {
const note = {
id : new Date(),
title : title,
content: content 

}

const updateNotes = [...notes, note]; // معامل النشر es6
setNotes(updateNotes);
setCreating(false);
setselecNote(note.id);

}


  const getAddNote = () => {
    return (
      <div>
        <h2>إضافة ملاحظة جديدة</h2>
        <div>
          <input
            type="text"
            name="title"
            className="form-input mb-30"
            placeholder="العنوان"
            value={title}
            onChange={changeTitleHandler}
          />

          <textarea
            rows="10"
            name="content"
            className="form-input"
            placeholder="النص"
            value={content}
           onChange={changeContentHandler}
          />

          <a href="#" className="button green" onClick={saveNoteHandler}>
            حفظ
          </a>
        </div>
      </div>
    );
  };

  const getPreview = () => {


    return (
      <div>
        <div className="note-operations">
          <a href="#">
            <i className="fa fa-pencil-alt" />
          </a>
          <a href="#">
            <i className="fa fa-trash" />
          </a>
        </div>
        <div>
          <h2>عنوان ملاحظة خالد قرد </h2>
          <p>نص ملاحظة خالد دبرا مش حاب يتعلم </p>
        </div>
      </div>
    );
  };
const addNoteHandler = () => {
  setCreating(true);
}

  return (
    <div className="App">
      <div className="notes-section">
        <ul className="notes-list">
          <li className="note-item">ملاحظة رقم #1</li>
          <li className="note-item">ملاحظة رقم #2</li>
          <li className="note-item">ملاحظة رقم #3</li>
          <li className="note-item">ملاحظة رقم #4</li>
        </ul>
        <button className="add-btn" onClick={addNoteHandler}>+</button>
      </div>
      <Preview>
        {creating ? getAddNote() : getPreview()}
      </Preview>
    </div>
  );
}

export default App;
